import { useEffect, useState } from 'react';
import './style.scss'
import SingleCard from '~/components/Student/Game/SingleCard';
import { IoIosArrowBack } from "react-icons/io";
import { Link } from 'react-router-dom';
import  background from '~/components/asset/img/Rectangle 368.png' 
import  cogai26 from '~/components/asset/img/image 26.png' 


function Game() {
    const [cards, setCards] = useState([])
    const [turns, setTurns] = useState(0)
    const [choiceOne, setChoiceOne] = useState(null) 
    const [choiceTwo, setChoiceTwo] = useState(null)
    const [disabled, setDisabled] = useState(false)

    const cardImages = [
        { "src": "data:image/jpeg;base64,", matched: false},
        { "src": "data:image/jpeg;base64,", matched: false},
        { "src": "data:image/jpeg;base64,"},
        { "src": "https://i.pinimg.com/474x/56/1b/64/561b6478c1352784e0cd4c7030e416b4.jpg", matched: false},
        { "src": "https://i.pinimg.com/474x/f0/d6/f5/f0d6f5f1d78a908b4e4676a5d28428d6.jpg", matched: false},
        { "src": "https://i.pinimg.com/474x/14/63/c7/1463c787dbc0ed1d1e0f6ccd9878dabb.jpg", matched: false},
    ]
    const shuffleCards = () =>{
        const shuffleCards = [...cardImages,...cardImages]
            .sort(() => Math.random() - 0.5)
            .map((card) => ({...card, id:Math.random() }))

        setChoiceOne(null)
        setChoiceTwo(null)
        setCards(shuffleCards)
        setTurns(0)
    }

    const handleChoice = (card) => {
        choiceOne ? setChoiceTwo(card) : setChoiceOne(card)
    }

    useEffect(() => {
        if (choiceOne && choiceTwo){
            setDisabled(true)
            if (choiceOne.src === choiceTwo.src){
                setCards(prevCards => {
                    return prevCards.map(card => {
                        if (card.src === choiceOne.src) {
                            return {...card, matched: true}
                        }else{
                            return card
                        }
                    })
                })
                resetTurn()
                
            } else{
                setTimeout(() => resetTurn(), 1000)
                
            }
        }

    }, [choiceOne, choiceTwo])

    const resetTurn = () => {
        setChoiceOne(null)
        setChoiceTwo(null)
        setTurns(prevTurns => prevTurns + 1)
        setDisabled(false)
    }

    useEffect(() => {
        shuffleCards()
    }, [])

    return ( 
        <div className='game'>
            <img className='background' src={background}></img>
            <img className='background2' src={cogai26}></img>
            <div className='game_header'>
                <Link><div className='game_icon'><IoIosArrowBack/></div></Link>
                <div className='game_score'>Turn: <b>{turns}</b></div>
                <div className='game_score'>Score: <b>3</b></div>
            </div>
            <div className="App">
                <div className='game_reset'><button onClick={shuffleCards}>New Game</button></div>

                <div className='card_grid'>
                    {cards.map(card => (
                        <SingleCard 
                            key={card.id} 
                            card={card}
                            handleChoice={handleChoice}
                            flipped={card === choiceOne || card === choiceTwo || card.matched}
                            disabled={disabled}
                            />
                    ))}
                </div>
            </div>
        </div>
     );
}

export default Game;